mapper.select:
select sid,,od
from t_section
where 1 = 1

    and sid = #{sid}


    and secname = #{secname}


    and detailname = #{detailname}


    and od = #{od}

select uid ,sid ,cursection
from t_user_section
where

    uid  = #{uid}


    and sid = #{sid}


    and cursection = #{cursection}
